import React from "react";
import styles from "./Dialogs.module.css";
import {Col, Row} from "react-bootstrap";
import {NavLink} from "react-router-dom";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";
import {sendMessageCreator, updateNewMessageBody} from "../../redux/dialogs-reducer";
import Dialogs from "./Dialogs";
import StoreContext from "../../StoreContext";


const DialogsContainer = (props) => {

    let state = props.store.getState().messagesPage

    let DialogsElements = state.dialogsData.map(el => {
        return (
            <DialogItem name={el.name} id={el.id}/>
        )
    })
    let MessagesElements = state.messagesData.map(el => {
        return (
            <Message text={el.message} id={el.id}/>
        )
    })

    let onSendMessageClick = () =>{
        props.store.dispatch(sendMessageCreator())
    }
    let onNewMessageChange = (body) =>{
        props.store.dispatch(updateNewMessageBody(body))
    }



    return (
        <StoreContext.Consumer>{
            (store) => {
                let state = props.store.getState().messagesPage

                let DialogsElements = state.dialogsData.map(el => {
                    return (
                        <DialogItem name={el.name} id={el.id}/>
                    )
                })
                let MessagesElements = state.messagesData.map(el => {
                    return (
                        <Message text={el.message} id={el.id}/>
                    )
                })

                let onSendMessageClick = () =>{
                    props.store.dispatch(sendMessageCreator())
                }
                let onNewMessageChange = (body) =>{
                    props.store.dispatch(updateNewMessageBody(body))
                }
            return  <Dialogs updateNewMessageBody={onNewMessageChange} sendMessage = {onSendMessageClick} messagesPage={props.store.getState().messagesPage}/>
            }
        }
        </StoreContext.Consumer>
    );
}
export default DialogsContainer;
