let store = {
    _state: {
        profilePage: {
            postData: [{id: 1, message: 'Hi, how are you?', likesCount: 45},
                {id: 2, message: "It's my first post!", likesCount: 25},],
            newPostText: 'Write new post'
        },
        messagesPage: {
            messagesData: [{id: 1, message: "Hi"},
                {id: 2, message: "How are you?"},
                {id: 3, message: "Im fine. What about you?"},
                {id: 4, message: "Yo"},
                {id: 5, message: "Yo"},
                {id: 6, message: "Yo"},],
            dialogsData: [{id: 1, name: "Vanya"},
                {id: 2, name: "Katya"},
                {id: 3, name: "Sveta"},
                {id: 4, name: "Natasha"},
                {id: 5, name: "Valera"},
                {id: 6, name: "Grisha"},],
        }
    },
    getState() {
        return this._state;
    },
    _callSubscriber() {
        console.log('nabil kot')
    },
    addPost() {
        let newPost = {
            id: 3,
            message: this._state.profilePage.newPostText,
            likesCount: 5
        }
        this._state.profilePage.postData.push(newPost);
        this._state.profilePage.newPostText = ('');
        this.renderEntireThree(this._state)
    },
    updateNewPostText(newText) {
        this._state.profilePage.newPostText = newText;
        this.renderEntireThree(this._state)
    },
    subscribe(observer) {
        this.renderEntireThree = observer
    }
}

export default store;
window.store = store;
