import React from "react";
import {Col, Row} from "react-bootstrap";
import * as axios from "axios";
import styles from './Login.module.css'
import {NavLink} from "react-router-dom";
import {Redirect} from "react-router-dom";

const Login = (props) =>{
    let state = props.authPage
    let onUpdateLogin = (event) =>{
        let body = event.target.value
        props.updateLogin(body)
    }
    let onUpdatePassword =  (event) =>{
        let body = event.target.value
        props.updatePassword(body)
    }
    let tryToLogIn = (event) =>{
        console.log(state)
        axios.post("http://localhost:5000/api/auth/login", {email: state.email, password: state.password}).then(response =>{
            console.log(response)
            // alert(response.status == 200)
            if(response.status == 200){
                props.updateToggle(true)
            }
        })
    }

    return(
        <Row>

            {
                state.toggle == false && <Col xs='12'>
                    <Row>
                        <Col xs='12'><h1>Log In</h1></Col>
                        <Col xs='12'><textarea className={styles.textarea} name="" id="" placeholder='Введите свою почту' onChange={onUpdateLogin}></textarea></Col>
                        <Col xs='12'><textarea className={styles.textarea} name="" id=""  placeholder='Введите пароль' onChange={onUpdatePassword}></textarea></Col>
                        <Col xs='3'><button onClick={tryToLogIn}>Войти</button></Col>
                        <Col xs='3' className={styles.signInButtonCol}><span>Зарегистрироваться</span></Col>
                    </Row>
                </Col>
            }
            {
                state.toggle == true && <Redirect to='/profile'/>
            }

        </Row>

    )
}

export default Login
