import React from "react";
import styles from "./Dialogs.module.css";
import {Col, Row} from "react-bootstrap";
import {NavLink} from "react-router-dom";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";


const Dialogs = (props) => {

    let DialogsElements = props.state.dialogsData.map(el => {
        return (
            <DialogItem name={el.name} id={el.id}/>
        )
    })
    let MessagesElements = props.state.messagesData.map(el => {
        return (
            <Message text={el.message} id={el.id}/>
        )
    })
    console.log(Dialogs);
    return (
        <Row>
            <Col xs='12' className={styles.dialogs}>
                <Row>
                    <Col xs='4' className={styles.dialogItems}>
                        <Row>
                            <Col xs='12' className={styles.dialog + ' ' + styles.active}>Dialogs</Col>
                        </Row>
                        {DialogsElements}
                    </Col>
                    <Col xs='8' className={styles.messages}>
                        <Row>
                            <Col xs='12'>
                                {MessagesElements}
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </Col>
        </Row>
    );
}
export default Dialogs;
