import React, {useState} from 'react';
import {Col, Row} from "react-bootstrap";
import styles from "./ProfileInfo.module.css";
import {Field, reduxForm} from "redux-form";
import {Textarea} from "../../common/FormControls/FormControls";
import {requiredField} from "../../../utils/validators/validators";

const ProfileStatus = (props) =>{

    let [editMode, setEditMode] = useState(false)
    const activateEditMode = () =>{
        setEditMode(true)
    }
    const deActivateEditMode = (value) =>{
        setEditMode(false)
        props.addStatus(value.NewStatusBody)
    }

        return(
            <Col xs='6'>
                <h1>{props.fullName}</h1>
                <span>Статус: </span>
                {
                    !editMode && <div><span className={styles.statusText} onDoubleClick={activateEditMode}>{props.status}</span></div>
                }
                {
                    editMode && <div><input autoFocus={false} onBlur={deActivateEditMode} type="text" value={props.status}/></div>
                }

                {props.contacts.twitter && <div><span>Contacts: </span><div><a href={props.contacts.twitter}>twitter</a></div></div>}
                <AddStatusReduxForm onSubmit={deActivateEditMode}/>
            </Col>
        )
    }
const addStatusForm = (props) =>{
    return(
        <form onBlur={props.handleSubmit}>
            <Field component={Textarea} placeholder={'Введите свой статус'} name={'NewStatusBody'} validate={[requiredField, maxLenngth10]}/>
            {/*<button>Add post</button>*/}
        </form>
    )
}
const  AddStatusReduxForm = reduxForm({form: 'newStatusReduxForm'})(addStatusForm)

export default ProfileStatus
