import React from 'react';
import {Col, Row} from 'react-bootstrap';
import styles from "./Myposts.module.css"
import Post from './Post/Post'
import {Field, reduxForm} from "redux-form";
import {requiredField} from "../../../utils/validators/validators";

const Myposts = (props) => {

    let postsElements = props.posts.map(el => {
        return (
            <Post message={el.message} likesCount={el.likesCount}/>
        )
    })

    let newElementPost = React.createRef()

    let onAddPost = (value) => {
        props.addPost(value.NewPostBody)
        // props.dispatch(addPostActionCreator())
    }
    let onPostChange = () => {
        let text = newElementPost.current.value;
        props.updateNewPostNext(text)
    }
    return (
        <Col xs='12'>
            <Row>
                <Col xs='12' className={styles.NewPostCol}>
                    {/*<textarea ref={newElementPost} value={props.newPostText} onChange={onPostChange}></textarea>*/}
                    {/*<button onClick={onAddPost}>Add post</button>*/}
                    <AddPostReduxForm onSubmit={onAddPost}/>
                </Col>
            </Row>
            {postsElements}
        </Col>
    );
}
const addPostForm = (props) =>{
    return(
        <form onSubmit={props.handleSubmit}>
            <Field component={"textarea"} placeholder={'Напишите свой первый пост'} name={'NewPostBody'} validate={requiredField}/>
            <button>Add post</button>
        </form>
    )
}
const  AddPostReduxForm = reduxForm({form: 'newPostReduxForm'})(addPostForm)
export default Myposts;
