import React from 'react';
import {Col, Row} from "react-bootstrap";
import styles from "./ProfileInfo.module.css";
import wallper from "../../../assets/images/london.jpg";

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    }
    activateEditMode = () => {
        debugger
        this.state.editMode = true
        this.forceUpdate()
    }

    render() {
        return (
            <div>
                {
                    !this.state.editMode && <div><span onDoubleClick={this.activateEditMode}>New status</span></div>
                }
                {
                    this.state.editMode && <div><input type="text" value='New status'/></div>
                }
                {/*<div><input type="text" value='New status'/></div>*/}
            </div>
        )
    }
}

export default ProfileStatus
