import profileReducer from "./profile-reducer";
import dialogsReducer from "./dialogs-reducer";
import sidebarReducer from "./sidebar-reducer";

const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY';
const SEND_MESSAGE = 'SEND-MESSAGE';

let store = {
    _state: {
        profilePage: {
            postData: [{id: 1, message: 'Hi, how are you?', likesCount: 45},
                {id: 2, message: "It's my first post!", likesCount: 25},],
            newPostText: 'Write new post'
        },
        messagesPage: {
            messagesData: [{id: 1, message: "Hi"},
                {id: 2, message: "How are you?"},
                {id: 3, message: "Im fine. What about you?"},
                {id: 4, message: "Yo"},
                {id: 5, message: "Yo"},
                {id: 6, message: "Yo"},],
            dialogsData: [{id: 1, name: "Vanya"},
                {id: 2, name: "Katya"},
                {id: 3, name: "Sveta"},
                {id: 4, name: "Natasha"},
                {id: 5, name: "Valera"},
                {id: 6, name: "Grisha"},],
            newMessageBody: 'ddd'
        },
        sidebar: {}
    },
    _callSubscriber() {
        console.log('kot')
    },


    getState() {
        return this._state;
    },
    subscribe(observer) {
        this._callSubscriber = observer
    },

    dispatch(action){
        this._state.profilePage = profileReducer(this._state.profilePage, action)
        this._state.messagesPage = dialogsReducer(this._state.messagesPage, action)
        this._state.sidebar = sidebarReducer(this._state.sidebar, action)


        if (action.type === ADD_POST){
            let newPost = {
                id: 3,
                message: this._state.profilePage.newPostText,
                likesCount: 5
            }
            this._state.profilePage.postData.push(newPost);
            this._state.profilePage.newPostText = ('');
            this._callSubscriber(this._state)
            alert(action)
        }else if (action.type === UPDATE_NEW_POST_TEXT){
            this._state.profilePage.newPostText = action.newText;
            this._callSubscriber(this._state)
            alert(action.newText)
        }else if(action.type === UPDATE_NEW_MESSAGE_BODY){
            store._state.messagesPage.newMessageBody = action.newText
            this._callSubscriber(this._state)
            console.log(action.newText)
            console.log(store._state.messagesPage.newMessageBody)
        }else if(action.type === SEND_MESSAGE){
            let newText = store._state.messagesPage.newMessageBody;
            console.log(11)
            store._state.messagesPage.newMessageBody = ''
            this._state.messagesPage.messagesData.push({id: 10, message: newText})
            console.log(store._state.messagesPage.newMessageBody)
            this._callSubscriber(this._state)
        }
    }
}
export const sendMessageCreator = () => {
    return {
        type: SEND_MESSAGE
    }
}
export const updateNewMessageBody = (body) => {
    return {
        type: UPDATE_NEW_MESSAGE_BODY,
        newText: body
    }
}

export const addPostActionCreator = () => {
    return {
        type: ADD_POST
    }
}
export const updateNewPostTextActionCreator = (text) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        newText: text
    }
}



export default store;
window.store = store;
