import React from "react";
import {Col, Row} from "react-bootstrap";
import * as axios from "axios";


const Login = (props) =>{
    let userLogin = document.getElementsByClassName('userLogin')
    let userPassword = document.getElementsByClassName('userPassword').value
    // userLogin = userLogin.current.value
    // userPassword = userPassword.current.value

    let tryToLogIn = () =>{
        let userLoginn = userLogin
        let userPasswordd = userPassword
        console.log(userLogin, userPassword)
        axios.post("http://localhost:5000/api/auth/login", {email: userLoginn, password: userPasswordd}).then(response =>{
            console.log(response)

        })
    }

    return(
        <Row>
            <Col xs='12'><h1>Login In</h1></Col>
            <Col xs='12'>
                <Row>
                    <Col xs='12' classNAme="userLogin"><textarea name="" id="" placeholder='Введите свою почту'></textarea></Col>
                    <Col xs='12' className="userPassword"><textarea name="" id=""  placeholder='Введите пароль'></textarea></Col>
                    <button onClick={tryToLogIn}>Войти</button>
                </Row>
            </Col>
        </Row>

    )
}

export default Login
