import React from 'react';
import {Col, Row} from "react-bootstrap";
import styles from "./ProfileInfo.module.css";
import wallper from "../../../assets/images/london.jpg";

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    }
    activateEditMode = () => {
        this.setState({
            editMode: true
        })
    }
    deActivateEditMode = () => {
        this.setState({
            editMode: false
        })
    }

    render() {
        return (
            <Col>
                {
                    !this.state.editMode && <div><span onDoubleClick={this.activateEditMode}>{'Статус: ' + this.props.status}</span></div>
                }
                {
                    this.state.editMode && <div><input autoFocus={true} onBlur={this.deActivateEditMode} type="text" value={this.props.status}/></div>
                }
            </Col>
        )
    }
}

export default ProfileStatus
