 // import {renderEntireThree} from "../render";

let renderEntireThree = () =>{
    console.log('nabil kot')
}
let state = {
    profilePage: {
        postData: [{id: 1, message: 'Hi, how are you?', likesCount: 45},
            {id: 2, message: "It's my first post!", likesCount: 25},],
        newPostText: 'Write new post'
    },
    messagesPage: {
        messagesData: [{id: 1, message: "Hi"},
            {id: 2, message: "How are you?"},
            {id: 3, message: "Im fine. What about you?"},
            {id: 4, message: "Yo"},
            {id: 5, message: "Yo"},
            {id: 6, message: "Yo"},],
        dialogsData: [{id: 1, name: "Vanya"},
            {id: 2, name: "Katya"},
            {id: 3, name: "Sveta"},
            {id: 4, name: "Natasha"},
            {id: 5, name: "Valera"},
            {id: 6, name: "Grisha"},],
    }
}
export let addPost = () => {
    let newPost = {
        id: 3,
        message: state.profilePage.newPostText,
        likesCount: 5
    }
    state.profilePage.postData.push(newPost);
    state.profilePage.newPostText = ('');
    renderEntireThree(state)
}

export let updateNewPostText = (newText) => {
    state.profilePage.newPostText = newText;
    renderEntireThree(state)
}


export default state;
