import React from "react";
import styles from './users.module.css?ver=123'
import * as axios from "axios";



class Users extends React.Component {

    componentDidMount() {
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
            this.props.setUsersAC(response.data.items)
        })
    }

    render() {
        let pagesCount =this.props.totalUsersCount
        return (
            <div>
                <div>
                    <span className={styles.selectedPage}>1</span>
                    <span className={styles.selectedPage}>2</span>
                    <span className={styles.selectedPage}>3</span>
                    <span className={styles.selectedPage}>4</span>
                    <span className={styles.selectedPage}>5</span>
                    <span className={styles.selectedPage}>6</span>
                </div>

                {
                    this.props.users.map(el => <div key={el.id}>
                <span>
                    <div>
                        <img src={el.photos.small != null ? el.photos.small : 'nagievjpg.jpg'} alt=""
                             className={styles.userPhoto}/>
                    </div>
                    <div>
                        {el.followed ? <button onClick={() => {
                            this.props.unfollowAC(el.id)
                        }}>Followed</button> : <button onClick={() => {
                            this.props.followAC(el.id)
                        }}>Unfollwed</button>}
                    </div>
                </span>
                            <span>
                    <span>
                        <div>{el.name}</div>
                        <div>{el.status}</div>
                        </span><span>
                        <div>{"el.location.city"}</div>
                        <div>{"el.location.country"}</div>
                        </span>
                </span>
                        </div>
                    )
                }
            </div>
        )
    }
}

export default Users;
