import React from "react";
import {Col, Row} from "react-bootstrap";
import * as axios from "axios";


const Login = (props) =>{
    let userLogin = React.createRef()
    let userPassword = React.createRef()
    // userLogin = userLogin.current.value
    // userPassword = userPassword.current.value

    let tryToLogIn = () =>{
        userLogin = userLogin.current.value
        userPassword = userPassword.current.value
        axios.post("http://localhost:5000/api/auth/login", {email: userLogin, password: userPassword}).then(response =>{
            console.log(response)
        })
    }

    return(
        <Row>
            <Col xs='12'><h1>Login In</h1></Col>
            <Col xs='12'>
                <Row>
                    <Col xs='12' ref={userLogin}><textarea name="" id="" placeholder='Введите свою почту'></textarea></Col>
                    <Col xs='12' ref={userPassword}><textarea name="" id=""  placeholder='Введите пароль'></textarea></Col>
                    <button onClick={tryToLogIn}>Зарегистрироваться</button>
                </Row>
            </Col>
        </Row>

    )
}

export default Login
