import React from "react";
import styles from "./Dialogs.module.css";
import {Col, Row} from "react-bootstrap";
import {NavLink} from "react-router-dom";
import DialogItem from "./DialogItem/DialogItem";
import Message from "./Message/Message";

// const DialogItem = (props) =>{
//     return(
//         <Row>
//             <Col xs='12' className={styles.dialog}><NavLink to={'/dialogs/' + props.id}>{props.name}</NavLink></Col>
//         </Row>
//     )
// }
// const Meassage = (props) =>{
//     return(
//         <Col xs='12' className={styles.message}>{props.text}</Col>
//     );
// }

const Dialogs = (props) => {
    let dialogsData = [
        {id: 1, name: "Vanya"},
        {id: 2, name: "Katya"},
        {id: 3, name: "Sveta"},
        {id: 4, name: "Natasha"},
        {id: 5, name: "Valera"},
        {id: 6, name: "Grisha"},
    ]

    let messagesData = [
        {id: 1, message: "Hi"},
        {id: 2, message: "How are you?"},
        {id: 3, message: "Im fine. What about you?"},
        {id: 4, message: "Yo"},
        {id: 5, message: "Yo"},
        {id: 6, message: "Yo"},
    ]
    let DialogsElements = dialogsData.map(el =>{
        return(
            <DialogItem name={el.name} id={el.id}/>
        )
    })
    let MessagesElements = messagesData.map(el =>{
        return(
            <Message text ={el.message} id={el.id}/>
            )
    })
    console.log(Dialogs);
    return (
        <Row >
            <Col xs='12' className={styles.dialogs}>
                <Row>
                    <Col xs='4' className={styles.dialogItems}>
                        <Row>
                            <Col xs='12' className={styles.dialog + ' ' + styles.active}>Dialogs</Col>
                        </Row>
                        {DialogsElements}
                    </Col>
                    <Col xs='8' className={styles.messages}>
                        <Row>
                            {MessagesElements}
                        </Row>
                    </Col>
                </Row>
            </Col>
        </Row>
    );
}
export default Dialogs;
