import React from "react";
import styles from './users.module.css?ver=123'
import * as axios from "axios";



class Users extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsersAC(response.data.items)
            this.props.setUsersAC(response.data.items)
        })
    }
    onPageChanged (pageNumber) {
        this.props.setCurrentPageAC(pageNumber)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {
            this.props.setUsersAC(response.data.items)
        })
    }
    render() {
        let pagesCount =Math.ceil(this.props.totalUsersCount/this.props.pageSize)
        let pages = [];
        for (let i = 1; i<=pagesCount;i++){
            pages.push(i)
        }
        return (

            <div>
                <div>
                    {pages.map(el =>{
                        return <span className={this.props.currentPage === el  && styles.selectedPage} onClick= {(e) =>{this.onPageChanged(el); }}>{el}</span>
                    })}
                </div>

                {
                    this.props.users.map(el => <div key={el.id}>
                <span>
                    <div>
                        <img src={el.photos.small != null ? el.photos.small : 'nagievjpg.jpg'} alt=""
                             className={styles.userPhoto}/>
                    </div>
                    <div>
                        {el.followed ? <button onClick={() => {
                            this.props.unfollowAC(el.id)
                        }}>Followed</button> : <button onClick={() => {
                            this.props.followAC(el.id)
                        }}>Unfollwed</button>}
                    </div>
                </span>
                            <span>
                    <span>
                        <div>{el.name}</div>
                        <div>{el.status}</div>
                        </span><span>
                        <div>{"el.location.city"}</div>
                        <div>{"el.location.country"}</div>
                        </span>
                </span>
                        </div>
                    )
                }
            </div>
        )
    }
}

export default Users;
