import React from "react";
import styles from './users.module.css?ver=123'


let Users = (props) => {

    props.setUsersAC(
        [{id: 1, photoUrl: 'nagievjpg.jpg', followed: true, fullName: "Vanya", status: `it's me`, location: {city: 'Minsk', country: 'Belarus'}},
            {id: 2, photoUrl: 'nagievjpg.jpg', followed: false, fullName: "Katya", status: `it's me to`, location: {city: 'Moskow', country: 'Russia'}},
            {id: 3, photoUrl: 'nagievjpg.jpg', followed: true, fullName: "Petya", status: `no, it's me`, location: {city: 'Kiev', country: 'Ukraine'}},
            {id: 4, photoUrl: 'nagievjpg.jpg', followed: false, fullName: "Vasya", status: `IT'S ME`, location: {city: 'Saint-P', country: 'Russia'}}
            ]
    )

    return (

        props.users.map(el =><div key={el.id}>
                <span>
                    <div>
                        <img src={el.photoUrl} alt="" className={styles.userPhoto}/>
                    </div>
                    <div>
                        {el.followed ? <button  onClick={ () => {props.unfollowAC(el.id)}}>Followed</button> : <button onClick={ () => {props.followAC(el.id)}}>Unfollwed</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{el.fullName}</div>
                        <div>{el.status}</div>
                        </span><span>
                        <div>{el.location.city}</div>
                        <div>{el.location.country}</div>
                        </span>
                </span>
            </div>
        )
    )
}

export default Users;
