const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS'

const initialState = {
    users: [
        {id: 1, photoUrl: 'nagievjpg.jpg', followed: true, fullName: "Vanya", status: `it's me`, location: {city: 'Minsk', country: 'Belarus'}},
        {id: 2, photoUrl: 'nagievjpg.jpg', followed: false, fullName: "Katya", status: `it's me to`, location: {city: 'Moskow', country: 'Russia'}},
        {id: 3, photoUrl: 'nagievjpg.jpg', followed: true, fullName: "Petya", status: `no, it's me`, location: {city: 'Kiev', country: 'Ukraine'}},
        {id: 4, photoUrl: 'nagievjpg.jpg', followed: false, fullName: "Vasya", status: `IT'S ME`, location: {city: 'Saint-P', country: 'Russia'}}
    ]
}

const userReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id == action.userId) {
                        return {...u, followed: true}
                    }
                    return u
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id == action.userId) {
                        return {...u, followed: false}
                    }
                    return u
                })
            }
        case SET_USERS:
            return {
                ...state,
                users: [...state.users, ...action.users]
            }
    }
}

export const followAC = (userId) => {
    return {
        type: FOLLOW,
        userId: userId
    }
}
export const unfollowAC = (userId) => {
    return {
        type: UNFOLLOW,
        userId: userId
    }
}
export const setUsersAC = (users) => {
    return {
        type: SET_USERS,
        users: users
    }
}

export default userReducer
