import React from "react";
import styles from "./users.module.css";


let Users = (props) => {

    let pagesCount =Math.ceil(props.totalUsersCount/props.pageSize)
    let pages = [];
    for (let i = 1; i<=pagesCount;i++){
        pages.push(i)
    }
    
    return (
        <div>
            <div>
                {pages.map(el => {
                    return <span className={props.currentPage === el && styles.selectedPage} onClick={() => { props.onPageChanged(el);}}>{el}</span>
                })}
            </div>

            {
                props.users.map(el => <div key={el.id}>
                <span>
                    <div>
                        <img src={el.photos.small != null ? el.photos.small : 'nagievjpg.jpg'} alt=""
                             className={styles.userPhoto}/>
                    </div>
                    <div>
                        {el.followed ? <button onClick={() => {
                            props.unfollowAC(el.id)
                        }}>Followed</button> : <button onClick={() => {
                            props.followAC(el.id)
                        }}>Unfollwed</button>}
                    </div>
                </span>
                        <span>
                    <span>
                        <div>{el.name}</div>
                        <div>{el.status}</div>
                        </span><span>
                        <div>{"el.location.city"}</div>
                        <div>{"el.location.country"}</div>
                        </span>
                </span>
                    </div>
                )
            }
        </div>
    )
}
export default Users
