        //                     <NavLink to={'/profile' + '/' + el.id}>
        //                         <img src={el.photos.small != null ? el.photos.small : nagiev} alt="" className={styles.userPhoto}/>
        //                          </NavLink>
        //                 </div>
        //                 <div>
        //                     {el.followed ? <button onClick={() => {
        //                         props.unfollow(el.id)
        //                     }}>Followed</button> : <button onClick={() => {
        //                         props.follow(el.id)
        //                     }}>Unfollowed</button>}
        //                 </div>
