import React from "react";
import styles from './users.module.css?ver=123'
import * as axios from "axios";


let Users = (props) => {
    if (props.users.length === 0){
        console.log(axios.get.then)
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
            debugger
            // console.log(response)
            props.setUsersAC(response.data.items)
        })
        // let a =axios.get('https://social-network.samuraijs.com/api/1.0/users');
        // console.log(a)
    }


    return (

        props.users.map(el =><div key={el.id}>
                <span>
                    <div>
                        <img src={el.photos.small != null ? el.photos.small: 'nagievjpg.jpg'} alt="" className={styles.userPhoto}/>
                    </div>
                    <div>
                        {el.followed ? <button  onClick={ () => {props.unfollowAC(el.id)}}>Followed</button> : <button onClick={ () => {props.followAC(el.id)}}>Unfollwed</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{el.name}</div>
                        <div>{el.status}</div>
                        </span><span>
                        <div>{"el.location.city"}</div>
                        <div>{"el.location.country"}</div>
                        </span>
                </span>
            </div>
        )
    )
}

export default Users;
