import React from "react";
import {Col,Row} from "react-bootstrap";
import styles from "../Login/Login.module.css";
import * as axios from "axios";
let SignIn = (props) =>{

    console.log(props.signInPage)
    let state = props.signInPage
    let onUpdatePassword = (event) => {
        let body = event.target.value
        props.updatePassword(body)
    }
    let onUpdateLogin = (event) => {
        let body = event.target.value
        props.updateLogin(body)
    }
    let onRegistration = () =>{
        axios.post("http://localhost:5000/api/auth/register", {
            email: state.email,
            password: state.password
        }).then(response => {
            if (response.status == 200) {
            }
        })
    }
    return(
        <Row>
            <Col xs='12'><h1>Регистрация</h1></Col>
            <Col xs='12'>
                <textarea className={styles.textarea} name="" id="" placeholder='Придумайте логин' onChange={onUpdateLogin}></textarea>
            </Col>
            <Col xs='12'>
                <textarea className={styles.textarea} name="" id="" placeholder='Придумайте пароль' onChange={onUpdatePassword}></textarea>
            </Col>
            <Col xs='12'>
                <button>Создать аккаунт</button>
            </Col>
        </Row>
    )
}
export default SignIn
