import React from "react";
import styles from './users.module.css?ver=123'
import * as axios from "axios";


let Users = (props) => {
console.log(props.users)
    if (props.users.length == 0){
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response =>{

            props.setUsersAC(response.items)
        })

    }


    return (

        props.users.map(el =><div key={el.id}>
                <span>
                    <div>
                        <img src={el.photoUrl} alt="" className={styles.userPhoto}/>
                    </div>
                    <div>
                        {el.followed ? <button  onClick={ () => {props.unfollowAC(el.id)}}>Followed</button> : <button onClick={ () => {props.followAC(el.id)}}>Unfollwed</button>}
                    </div>
                </span>
                <span>
                    <span>
                        <div>{el.fullName}</div>
                        <div>{el.status}</div>
                        </span><span>
                        <div>{el.location.city}</div>
                        <div>{el.location.country}</div>
                        </span>
                </span>
            </div>
        )
    )
}

export default Users;
