import React from "react";
import {compose} from "redux";
import {connect} from "react-redux";
import {withAuthRedirect} from "../../hoc/withAuthRedirect";

import SignIn from "./SigIn";
import {updateLoginReg, updatePasswordReg, updateRegSucsess} from "../../redux/register-reducer";

let mapDispatchToProps = (dispatch) =>{

    return {
        updatePassword: (password) =>{
            dispatch(updatePasswordReg(password))
        },
        updateLogin: (login) => {
            dispatch(updateLoginReg(login))
        },
        updateToggle: (bool) =>{
            dispatch(updateRegSucsess(bool))
        },
        // updateDescription: (info) =>{
        //     dispatch(addDescription(info))
        // }
    }
}
let mapStateToProps = (state) =>{
    return {
        signInPage: state.signInPage
    }
}

export default compose(connect(mapStateToProps, mapDispatchToProps),withAuthRedirect)(SignIn);
