import React, {useState} from 'react';
import {Col, Row} from "react-bootstrap";
import styles from "./ProfileInfo.module.css";

const ProfileStatus = (props) =>{
    // let stateWithSetState = useState(false)
    // let editMode = stateWithSetState[0]
    // let setEditMode = stateWithSetState[0]
    let [editMode, setEditMode] = useState(false)
    console.log(editMode,setEditMode)
    // activateEditMode = () => {
    //     this.setState({
    //         editMode: true
    //     })
    // }
    // deActivateEditMode = () => {
    //     this.setState({
    //         editMode: false
    //     })
    // }
    const activateEditMode = () =>{
        setEditMode(true)
    }
    // console.log(props)
        return(
            <Col xs='6'>
                <h1>{props.fullName}</h1>
                <span>Статус: </span>
                {
                    !editMode && <div><span className={styles.statusText} onDoubleClick={this.activateEditMode}>{props.status}</span></div>
                }
                {
                    editMode && <div><input autoFocus={false} onBlur={this.deActivateEditMode} type="text" value={props.status}/></div>
                }

                {this.props.contacts.twitter && <div><span>Contacts: </span><div><a href={props.contacts.twitter}>twitter</a></div></div>}

            </Col>
        )
    }


export default ProfileStatus
