import React, {useState} from "react";
import styles from "../../Users/users.module.css";

import {Container, Col, Row} from "react-bootstrap";


let Paginator = ({currentPage, onPageChanged,totalUsersCount,pageSize, portionSize = 10}) => {

    let pagesCount = Math.ceil(totalUsersCount / pageSize)
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }
    console.log(currentPage)
    console.log(onPageChanged)
    console.log(totalUsersCount)
    console.log(pageSize)
    // console.log(usersProtionCount)
    let portionCount = Math.ceil(pagesCount/portionSize)
    let [portionNumber, setPortionNumber] = useState(1)
    let leftPortionNumber = (portionNumber-1)*portionSize+1
    let rightPortionNumber = (portionNumber+1)*portionSize+1

    return (
                <Row>
                    <Col xs='12'>
                        {pages.map(el => {
                            return <span className={currentPage === el && styles.selectedPage} onClick={(event) => {onPageChanged(el);}}>{el}</span>
                        })}
                    </Col>
                </Row>
    )
}
export default Paginator
