import React from 'react';
import {Col, Row} from "react-bootstrap";
import styles from "./ProfileInfo.module.css";
import {Field, reduxForm} from "redux-form";
import {Textarea} from "../../common/FormControls/FormControls";
import {requiredField} from "../../../utils/validators/validators";

class ProfileStatus extends React.Component {
    state = {
        editMode: false
    }
    activateEditMode = () => {
        this.setState({
            editMode: true
        })
    }
    deActivateEditMode = () => {
        this.setState({
            editMode: false
        })
    }

    render() {
        return (
            <Col xs='6'>
                <h1>{this.props.fullName}</h1>
                <span>Статус: </span>
                {
                    !this.state.editMode && <div><span className={styles.statusText} onDoubleClick={this.activateEditMode}>{this.props.status}</span></div>
                }
                {
                    this.state.editMode && <div><input autoFocus={false} onBlur={this.deActivateEditMode} type="text" value={this.props.status}/></div>
                }

                {this.props.contacts.twitter && <div><span>Contacts: </span><div><a href={this.props.contacts.twitter}>twitter</a></div></div>}

            </Col>
        )
    }
}
const addStatus = (props) =>{
    return(
        <form onSubmit={props.handleSubmit}>
            <Field component={Textarea} placeholder={'Введите свой статус'} name={'NewStatusBody'} validate={[requiredField, maxLenngth10]}/>
        </form>
    )
}
const  AddStatusReduxForm = reduxForm({form: 'newPostReduxForm'})(addStatus)

export default ProfileStatus
