const SET_USER_DATA = 'SET-USER-DATA'
const LOG_IN = 'LOG-IN'
const REGISTER = 'REGISTER'

const initialState = {
    email: null,
    password: null
}

const authReducer = (state = initialState, action) => {

    switch (action.type) {
        case LOG_IN:
            return {
                ...state,
                ...action.data
            }
        case REGISTER:
            return {
                ...state,
                ...action.data
            }
        default:
            return state
    }
}

// export const setAuthUserData = (userId,email,login) => {
//     return {
//         type: SET_USER_DATA,
//         data: {userId,email,login}
//     }
// }
export const setVerificyCreator = (password,email) => {
    return {
        type: LOG_IN,
        data: {password,email}
    }
}
export const setAccountCreator = (password,email) => {
    return {
        type: REGISTER,
        data: {password,email}
    }
}


export default authReducer
