import React from "react";
import {Col, Row} from "react-bootstrap";
import * as axios from "axios";


const Login = (props) =>{
    // let userLogin = React.createRef()
    // let userPassword = React.createRef()
    // userLogin = userLogin.current.value
    // userPassword = userPassword.current.value
    let state = props.authPage
    debugger
    let onUpdateLogin = (event) =>{
        let body = event.target.value
        props.updateLogin(body)
    }
    let onUpdatePassword =  (event) =>{
        let body = event.target.value
        props.updatePassword(body)
    }
    let tryToLogIn = (event) =>{
        console.log(state)
        // axios.post("http://localhost:5000/api/auth/login", {email: userLoginn, password: userPasswordd}).then(response =>{
        //     console.log(response)
        //
        // })
    }

    return(
        <Row>
            <Col xs='12'><h1>Login In</h1></Col>
            <Col xs='12'>
                <Row>
                    <Col xs='12'><textarea name="" id="" placeholder='Введите свою почту' onChange={onUpdateLogin}></textarea></Col>
                    <Col xs='12'><textarea name="" id=""  placeholder='Введите пароль' onChange={onUpdatePassword}></textarea></Col>
                    <button onClick={tryToLogIn}>Войти</button>
                </Row>
            </Col>
        </Row>

    )
}

export default Login
