import React from "react";
import {connect} from "react-redux";
// import preloader from "../../../public/Double Ring-1s-200px.svg"
import {
    followAC,
    setCurrentPageAC,
    setTotalUsersCountAC,
    setUsersAC,
    toggleIsFetchingAC,
    unfollowAC
} from "../../redux/users-reducer";
import * as axios from "axios";
import Users from "./Users";
import Preloader from "../common/preloader/Preloader";



class UsersContainer extends React.Component {
    componentDidMount() {
        this.props.toggleIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`).then(response => {
            this.props.toggleIsFetching(false)
            this.props.setUsersAC(response.data.items)
            this.props.setTotalUsersCountAC(response.data.totalCount)
        })
    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPageAC(pageNumber)
        this.props.toggleIsFetching(true)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`).then(response => {
            this.props.toggleIsFetching(false)
            this.props.setUsersAC(response.data.items)
        })
    }

    render() {
        // console.log(this.props)
        // {this.props.isFetching ? <img src="" alt=""/> : null}
        return <>
            {this.props.isFetching ? <Preloader /> : null}
            <Users totalUsersCount={this.props.totalUsersCount}
                      pageSize={this.props.pageSize}
                      currentPage={this.props.currentPage}
                      onPageChanged={this.onPageChanged}
                      users={this.props.users}
                      followAC = {this.props.followAC}
                      unfollowAC = {this.props.unfollowAC}
                      isFetching ={this.props.isFetching}/>
                      </>
    }
}

let mapStateToProps =(state) =>{
    return{
        users: state.usersPage.users,
        pageSize: state.usersPage.pageSize,
        totalUsersCount: state.usersPage.totalUsersCount,
        currentPage: state.usersPage.currentPage,
        isFetching: state.usersPage.isFetching
    }
}
let mapDispatchToProps =(dispatch) =>{

    return{
        followAC: (userId) =>{
            dispatch(followAC(userId))
        },
        unfollowAC: (userId) => {
            dispatch(unfollowAC(userId))
        },
        setUsersAC: (users) =>{
            dispatch(setUsersAC(users))
        },
        setCurrentPageAC: (pageNumber) =>{
            dispatch(setCurrentPageAC(pageNumber))
        },
        setTotalUsersCountAC: (totalCount) =>{
            dispatch(setTotalUsersCountAC(totalCount))
        },
        toggleIsFetching: (isFetching) =>{
            dispatch(toggleIsFetchingAC(isFetching))
        }
    }
}

 // let UsersContainer = connect(mapStateToProps, mapDispatchToProps)(UsersContainer)
export default connect(mapStateToProps, mapDispatchToProps)(UsersContainer);
